mapper.update:
update sky_take_out.setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, image = #{image}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name
category_name from sky_take_out.setmeal s left join sky_take_out.category c on s.category_id = c.id


        and s.category_id = #{categoryId}


        s.name like concat('%',#{name},'%')


        and s.status = #{status}


order by update_time desc
