mapper.select:
select s.id, s.name, no, gender, phone, id_card idCard, is_college isCollege, address, degree, graduation_date graduationDate, clazz_id clazzId, violation_count violationCount, violation_score violationScore, s.create_time createTime, s.update_time updateTime, c.name clazzName from student s left join clazz c on s.clazz_id = c.id
name = #{name}
and degree = #{degree}
and clazz_id = #{clazzId}
